	function getHotelList($param_list){		
		global $hotel_list1,$hotel_list2,$hotel_list3;
		
		if($param_list['city_id'] == 1)
			$banner = $hotel_list1;
		else if($param_list['city_id'] == 3)
			$banner = $hotel_list3;
		else 
			$banner = $hotel_list2;
			
		$hotelList = getHotelListByLanguage($param_list,$banner);
		
		if($param_list['location_code'] != -1 && count($hotelList) > 0){
			$key = 0;
			$hotel_list = array();
			foreach($hotelList as $list){
				if($param_list['location_code'] == $list['location_code']){				
					$hotel_list[$key] = $list;
					$key = $key +1;
				}
			}
			$hotelList = $hotel_list;
		}
		if($param_list['star'] != -1 && count($hotelList) > 0){
			$key = 0;
			$hotel_list = array();
			foreach($hotelList as $list){
				if($param_list['star'] == $list['star']){				
					$hotel_list[$key] = $list;
					$key = $key +1;
				}
			}
			$hotelList = $hotel_list;
		}
		if($param_list['keyword'] != "" && count($hotelList) > 0){
			$key = 0;
			$hotel_list = array();
			foreach($hotelList as $list){
				if(strstr($list['hotel_name'],$param_list['keyword'])){				
					$hotel_list[$key] = $list;
					$key = $key +1;
				}
			}
			$hotelList = $hotel_list;
		}
		if($param_list['min_price'] != -1 && $param_list['max_price'] != -1 && count($hotelList) > 0){  
			$key = 0;
			$hotel_list = array();
			foreach($hotelList as $list){
				if($list['price'] >= $param_list['min_price'] && $list['price'] < $param_list['max_price']){				
					$hotel_list[$key] = $list;
					$key = $key +1;
				}
			}
			$hotelList = $hotel_list;
		}
		return $hotelList;
	}
	
	function getHotelListByPageNo($param_list){
		global $smarty;
		$hotel_list = array();
		$hotelList = getHotelList($param_list);
		$total_page = count($hotelList);
		
		$key = 0;
		$i = 0;
		$start = ($param_list["page_from"]-1)*$param_list["page_count"];		
		$end = $param_list["page_to"]*$param_list["page_count"];
		if($total_page < $end)
			$end = $total_page;
		if($total_page > 0 && $start < $end){
			foreach($hotelList as $list){
				if($i >= $start && $i < $end){
					$hotel_list[$key] = $list;
					$key = $key + 1;
				}
				$i = $i +1;
			}
			$hotelList = $hotel_list;
		}
		reset($hotelList);
		$smarty->assign("total_page", (int)(($total_page+1)/$param_list["page_count"]));
		$smarty->assign("hotel_list", $hotelList);
	}
	
	function getHotelListByLanguage($param_list,$banner){
		$hotel_list = array();
		$key = 0;
		if($param_list['lang'] == "")
			$lang_id = $param_list['lang_id'];
		else{
			if($param_list['lang'] == "en-us")
				$lang_id = 1;
			else if($param_list['lang'] == "zh-tc")
				$lang_id = 2;
			else if($param_list['lang'] == "zh-sc")
				$lang_id = 3;
		}
		foreach($banner as $list){
			if($lang_id == 1 && $list['lang'] == "en-us"){
				$hotel_list[$key] = $list;
				$key = $key +1;
			}else if($lang_id == 2 && $list['lang'] == "zh-tc"){
				$hotel_list[$key] = $list;
				$key = $key +1;
			}else if($lang_id == 3 && $list['lang'] == "zh-sc"){
				$hotel_list[$key] = $list;
				$key = $key +1;
			}	
		}
		return $hotel_list;
	}
	
