			$lang_id = $param_list['lang_id'];
		else{
			if($param_list['lang'] == "en-us")
				$lang_id = 1;
			else if($param_list['lang'] == "zh-tc")
				$lang_id = 2;
			else if($param_list['lang'] == "zh-sc")
				$lang_id = 3;
		}
		foreach($bannerList as $list){
			if($lang_id == 1 && $list['lang'] == "en-us"){
				$banner_list[$key] = $list;
				$key = $key +1;
			}else if($lang_id == 2 && $list['lang'] == "zh-tc"){
				$banner_list[$key] = $list;
				$key = $key +1;
			}else if($lang_id == 3 && $list['lang'] == "zh-sc"){
				$banner_list[$key] = $list;
				$key = $key +1;
			}	
		}
		$bannerList = $banner_list;
		$smarty->assign("banner_count", count($bannerList));
		$smarty->assign("banner_list", $bannerList);
	}
	
